import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { Box, Button, Container, List, ListItem, ListItemText, Typography, TextField } from '@mui/material';
import { styled } from '@mui/system';

const Root = styled(Box)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  marginTop: theme.spacing(8),
}));

const UploadForm = styled('form')(({ theme }) => ({
  width: '100%',
  marginTop: theme.spacing(1),
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
}));

const UploadButton = styled(Button)(({ theme }) => ({
  marginTop: theme.spacing(2),
}));

const PDFList = styled(List)(({ theme }) => ({
  width: '100%',
  marginTop: theme.spacing(2),
}));

const Home = () => {
  const [pdfs, setPdfs] = useState([]);
  const [file, setFile] = useState(null);

  useEffect(() => {
    const fetchPdfs = async () => {
      const token = localStorage.getItem('token');
      const res = await axios.get('http://localhost:5000/api/pdfs', {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      setPdfs(res.data.pdfs);
    };
    fetchPdfs();
  }, []);

  const handleUpload = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('pdf', file);
    const token = localStorage.getItem('token');
    try {
      await axios.post('http://localhost:5000/api/pdfs/upload', formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      });
      setFile(null);
      // Refetch PDFs
      const res = await axios.get('http://localhost:5000/api/pdfs', {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      setPdfs(res.data.pdfs);
    } catch (error) {
      console.error('Error uploading PDF:', error);
    }
  };

  return (
    <Container component="main" maxWidth="md">
      <Root>
        <Typography component="h1" variant="h5">
          Upload PDF
        </Typography>
        <UploadForm onSubmit={handleUpload}>
          <TextField
            type="file"
            accept="application/pdf"
            onChange={(e) => setFile(e.target.files[0])}
            required
          />
          <UploadButton
            type="submit"
            variant="contained"
            color="primary"
          >
            Upload
          </UploadButton>
        </UploadForm>
        <Typography component="h2" variant="h6" style={{ marginTop: '20px' }}>
          Uploaded PDFs
        </Typography>
        <PDFList>
          {pdfs.map((pdf) => (
            <ListItem button component={Link} to={`/pdf/${pdf._id}`} key={pdf._id}>
              <ListItemText primary={pdf.filename} />
            </ListItem>
          ))}
        </PDFList>
      </Root>
    </Container>
  );
};

export default Home;
